def _try_parse_json_or_xml(res: requests.Response) -> Dict[str, Any]:
    text = res.text or ""
    try:
        return res.json()
    except Exception:
        pass

    try:
        import xml.etree.ElementTree as ET
        root = ET.fromstring(text)

        def find_text(xpath: str) -> str:
            el = root.find(xpath)
            return el.text.strip() if (el is not None and el.text) else ""

        result_code = find_text(".//resultCode")
        result_msg = find_text(".//resultMsg") or find_text(".//returnReason")
        return {"_xml": True, "resultCode": result_code, "resultMsg": result_msg, "_raw": text[:800]}
    except Exception:
        return {"_raw": text[:800]}


def _extract_items(data: Any) -> List[Dict[str, Any]]:
    if not isinstance(data, dict):
        return []

    if isinstance(data.get("result"), list):
        return data["result"]
    if isinstance(data.get("result"), dict):
        return [data["result"]]

    item = (
        data.get("response", {})
        .get("body", {})
        .get("items", {})
        .get("item")
    )
    if isinstance(item, list):
        return item
    if isinstance(item, dict):
        return [item]

    items = data.get("items")
    if isinstance(items, list):
        return items
    if isinstance(items, dict):
        return [items]

    return []


def _load_jobs_cache() -> Optional[List[Dict[str, Any]]]:
    try:
        if JOBS_CACHE_PATH.exists():
            payload = json.loads(JOBS_CACHE_PATH.read_text(encoding="utf-8"))
            if isinstance(payload, dict) and isinstance(payload.get("items"), list):
                return payload["items"]
    except Exception:
        return None
    return None


def _save_jobs_cache(items: List[Dict[str, Any]]) -> None:
    try:
        payload = {
            "saved_at": datetime.now().isoformat(timespec="seconds"),
            "items": items,
        }
        JOBS_CACHE_PATH.write_text(json.dumps(payload, ensure_ascii=False, indent=2), encoding="utf-8")
    except Exception:
        pass

