def _ensure_genai_configured() -> bool:
    global _GENAI_CONFIGURED
    if _GENAI_CONFIGURED:
        return True
    if not (_GENAI_OK and GOOGLE_API_KEY):
        return False
    try:
        genai.configure(api_key=GOOGLE_API_KEY)
        _GENAI_CONFIGURED = True
        return True
    except Exception:
        return False


